const AWS = require('aws-sdk');
const response = require('cfn-response');
const cognito = new AWS.CognitoIdentityServiceProvider();

exports.handler = async (event, context) => {
  console.log('Event:', JSON.stringify(event, null, 2));

  try {
    const { UserPoolId, LambdaFunctionArn } = event.ResourceProperties;

    if (event.RequestType === 'Create' || event.RequestType === 'Update') {
      console.log('Configuring Cognito PostConfirmation trigger...');

      await cognito.updateUserPool({
        UserPoolId: UserPoolId,
        LambdaConfig: {
          PostConfirmation: LambdaFunctionArn
        }
      }).promise();

      console.log('Successfully configured Cognito trigger');
      await response.send(event, context, response.SUCCESS, {});
    } else if (event.RequestType === 'Delete') {
      console.log('Removing Cognito PostConfirmation trigger...');

      await cognito.updateUserPool({
        UserPoolId: UserPoolId,
        LambdaConfig: {}
      }).promise();

      console.log('Successfully removed Cognito trigger');
      await response.send(event, context, response.SUCCESS, {});
    }
  } catch (error) {
    console.error('Error:', error);
    await response.send(event, context, response.FAILED, {});
  }
};
